import { GrayscaleFilter, BlurFilter, InvertFilter, SepiaFilter, ThresholdFilter, TextFilter, ColorizeFilter, ImageFilter } from 'pixel-flip';

import WatermarkFilter from '../component/filter/Watermark';

export default function filterNameToClass(filterName) {
    switch (filterName) {
        case 'grayscale':
            return GrayscaleFilter;
        case 'blur':
            return BlurFilter;
        case 'invert':
            return InvertFilter;
        case 'sepia':
            return SepiaFilter;
        case 'threshold':
            return ThresholdFilter;
        case 'text':
            return TextFilter;
        case 'color':
            return ColorizeFilter;
        case 'image':
            return ImageFilter;
        case 'watermark':
            return WatermarkFilter;
        default:
            return null;
    }
}